package com.example.demo.service;

import cn.hutool.http.HttpUtil;
import com.alibaba.fastjson.JSON;
import com.alibaba.fastjson.JSONObject;
import com.alibaba.fastjson.serializer.SerializerFeature;
import com.example.demo.consts.SecretConsts;
import com.example.demo.model.RepairRecordDo;
import com.example.demo.utils.SignUtil;
import org.springframework.stereotype.Service;

import java.util.*;

@Service
public class RepairRecordService {


    private final static String URL = "http://test-www.yrdcarlife.com/openapi/repairRecord/insert";



    public Object insert() {
        Date date = new Date();
        String time = String.valueOf(date.getTime());
        long nonce = SignUtil.getLong();
        RepairRecordDo repairRecord = getRepairRecod(time,nonce);
        repairRecord.setSign(sign(time,nonce));
        return HttpUtil.post(URL,JSON.toJSONString(repairRecord));
    }

    /**
     * 对象参数
     * @param time
     * @return
     */
    private static RepairRecordDo getRepairRecod(String time,long nonce){
        String carBase64 = "";
        String drvingBase64 = "";
        RepairRecordDo repairRecod = new RepairRecordDo();
        repairRecod.setAppId(SecretConsts.APPID);
        repairRecod.setSign("");
        repairRecod.setTimestamp(time);
        repairRecod.setNonce(nonce);
        repairRecod.setVin("WVWLJ57N4FV0288");
        repairRecod.setVpn("GK12001");
        repairRecod.setOrderCode("1");
        repairRecod.setRepairTime("2021-05-28 00:01:01");
        repairRecod.setRepairMileage("10");
        repairRecod.setServiceType("常规保养");
        repairRecod.setFuelType("A");
        repairRecod.setRequirement("需要全面检测");
        repairRecod.setFaultDesc("发动机故障");
        repairRecod.setNatureOfUse(1);
        repairRecod.setCarPhoto(carBase64);
        repairRecod.setDrivingPermitPhoto(drvingBase64);
        return repairRecod;
    }


    /**
     * 生成秘钥
     * @return
     */
    private static String sign(String time,long nonce){
        RepairRecordDo repairRecod = getRepairRecod(time,nonce);
        JSONObject object = JSONObject.parseObject(JSON.toJSONString(repairRecod, SerializerFeature.WriteMapNullValue));
        System.out.println(object.toJSONString());
        String sign = SignUtil.signature(object,SecretConsts.SECRET);
        System.out.println(sign);
        return sign;
    }






}
